import React from "react";
import PropTypes from "prop-types";
import { ListGroupItem, Slider } from "shards-react";
import placeholder from './placeholder-image.png';
import './ImagePosition.css';

import {
  Row, 
  Col,
  Card,
  CardHeader,
  CardBody,
  Form,
  FormGroup,
  FormInput,
  FormTextarea,
  Button
} from "shards-react";


const NewDraft = ({ title }) => (
  <Card small className="h-100">
    {/* Card Header */}
    <CardHeader className="border-bottom">
      <h6 className="m-0">{title}</h6>
    </CardHeader>

    <CardBody className="d-flex flex-column">
	<Row className="border-bottom py-2 bg-light">
	 <Col sm="6" className="d-flex mb-2 mb-sm-0">

            </Col>

			  <Slider
        connect
        start={[35, 65]}
        pips={{
          mode: "positions",
          values: [0, 25, 50, 75, 100],
          stepped: true,
          density: 5
        }}
        range={{ min: 0, max: 100 }}
      />   
          </Row>
      <Form className="quick-post-form">

         {/* Body */}
        <div>
          <img src={placeholder} width ="500" alt="N/A"  object-fit="cover" class="center"/>
        </div>

        {/* Create Draft */}
        <FormGroup className="mb-0">
          <Button theme="accent" type="submit">
            Upload FMI Log
          </Button>
        </FormGroup>
      </Form>
    </CardBody>
  </Card>
);

NewDraft.propTypes = {
  /**
   * The component's title.
   */
  title: PropTypes.string
};

NewDraft.defaultProps = {
  title: "Vizualize FMI Log"
};

export default NewDraft;
